def commented_colnames(ifile):
    """ Create column names list from first comment line of a datafile."""
    colnames = []
    with open(infile) as fi:
        for l in fi:
            if l.strip().startswith('#'):
                names = l.strip('#')
                colnames = [i for i in names.split()]
                break
    return colnames
